<template>
  <div class="corporate">
    <div class="container">
      <div class="corporate__pos position secondary">
        <nuxt-link class="corporate__pos-link position-link" :to="($i18n.locale === 'ua' ? '/ua' : '') + '/'">{{
          $t('home.position')
        }}</nuxt-link>
        <svg
          width="12"
          height="12"
          viewBox="0 0 12 12"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M7.00001 11L6.3 10.3L10.1 6.5H0V5.50002H10.1L6.3 1.7L7.00001 1.00002L12 6.00001L7.00001 11Z"
            fill="#fff"
          />
        </svg>
        <nuxt-link
          class="corporate__pos-link position-link"
          :to="$route.path"
          >{{ $t('corporate.title') }}</nuxt-link
        >
      </div>
    </div>
    <div class="corporate__top">
      <banners-top class="corporate__top-banner" :banner="topBanner" />
      <div class="container">
        <div class="corporate__top-info">
          <h2 class="corporate__top-title">{{ $t('corporate.title') }}</h2>
          <p class="corporate__top-text">
            {{ $t('corporate.text1') }}
          </p>
          <p class="corporate__top-text">
            {{ $t('corporate.text2') }}
          </p>
        </div>
      </div>
    </div>
    <div class="corporate__info">
      <div class="container">
        <my-popup
          v-if="showWriteModal"
          class="corporate-modal"
          @close="showWriteModal = false"
        >
          <validation-observer
            v-slot="{ invalid }"
            class="corporate-modal__form"
            tag="form"
            name="write"
            @submit.prevent="writeSubmit"
          >
            <h2 class="corporate-modal__title">
              {{ $t('corporate.writeUs.title') }}
            </h2>
            <validation-provider name="name" rules="required">
              <div slot-scope="{ errors }" class="corporate-modal__field">
                <my-input
                  v-model="writeName"
                  class="corporate-modal__input"
                  :placeholder="$t('corporate.writeUs.name')"
                  :errors="errors"
                />
              </div>
            </validation-provider>
            <validation-provider name="email" rules="required|email">
              <div slot-scope="{ errors }" class="corporate-modal__field">
                <my-input
                  v-model="writeEmail"
                  class="corporate-modal__input"
                  :placeholder="$t('corporate.writeUs.email')"
                  :errors="errors"
                />
              </div>
            </validation-provider>
            <validation-provider name="message" rules="required">
              <div slot-scope="{ errors }" class="corporate-modal__field">
                <my-input
                  v-model="writeMessage"
                  class="corporate-modal__input"
                  :placeholder="$t('corporate.writeUs.message')"
                  :errors="errors"
                />
              </div>
            </validation-provider>
            <my-button class="corporate-modal__btn" :disabled="invalid">{{
              $t('corporate.writeUs.submit')
            }}</my-button>
          </validation-observer>
        </my-popup>
