<script>
export default {
  async asyncData({ $readData, i18n }) {
    let topBanner
    let ecoBanner
    if (i18n.locale === 'ua') {
      topBanner = await $readData('banners/top/ua')
      ecoBanner = await $readData('banners/eco/ua')
    } else {
      topBanner = await $readData('banners/top')
      ecoBanner = await $readData('banners/eco')
    }
    return { topBanner, ecoBanner }
  },
  data() {
    return {
      selectedCorporate: 'supermarket',
      showWriteModal: false,
      writeName: '',
      writeEmail: '',
      writeMessage: '',
    }
  },
  watch: {
    async '$i18n.locale'() {
      if (this.$i18n.locale === 'ua') {
        this.topBanner = await this.$readData('banners/top/ua')
        this.ecoBanner = await this.$readData('banners/eco/ua')
      } else {
        this.topBanner = await this.$readData('banners/top')
        this.ecoBanner = await this.$readData('banners/eco')
      }
    },
  },
  methods: {
    writeSubmit() {
      const messageData = {
        name: this.writeName,
        email: this.writeEmail,
        message: this.writeMessage,
      }
      this.$writeData(`messages/${+new Date()}`, messageData)
      this.showWriteModal = false
      this.$toasted.success('Ваше сообщение доставлено.')
    },
  },
}
</script>

